import React, { Component } from "react";
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
import KryptoBird from '../abis/KryptoBird.json';

class App extends Component {

    async componentDidMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
    }
    //first up is to detect ethereum provider
    async loadWeb3() {
        const provider = await detectEthereumProvider()

        //modern browsers
        // if there is a provider then lets console.log that its working and access 
        // the window from the doc to set Web3 to the provider
        if (provider) {
            console.log('Ethereum wallet is connected')
            window.web3 = new Web3(provider)
        } else {
            console.log('No Ethereum wallet detected')
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3
        const accounts = web3.eth.getAccounts()
        this.setState({account: accounts})
        console.log(this.state.account)
    }

    constructor(props) {
        super(props);
        this.state = {
            account: ''
        }
    }

    render() {
        return (
            <div>
            <nav className = 'navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow'>
            <div lassName = 'navbar-brand col-sm-3 col-md-3 mr-0'
                style ={{color: 'white'}}>
                Krypto Birdz NFTs (Non-Fungible Token)
            </div>
            </nav>
                <h1>
                NFT Marketplace
                </h1>
            </div>
        )
    }
}

export default App;
